import React, { useEffect } from 'react'
import styled from 'styled-components'

const Black = styled.button`
  width: 40px;
  height: 130px;
  background: #93ac47;
  border: solid black 1px;
  box-shadow: 2px 5px;
  position: absolute;
  margin: 1px;
  margin-left: -20px;

  :active {
    background: #333;
  }
`

const White = styled.button`
  width: 60px;
  height: 200px;
  background: #3d7c4d;
  border: solid black 1px;
  box-shadow: 2px 5px;
  margin: 1px;
  margin-left: '-20px';
  box-sizing: border-box;

  :active {
    background: #eee;
  }
`

type Props = {
  color: string
  note: string
  key: string
  clickHandler: (note: string) => void
}

const Note: React.FC<Props> = ({ color, note, key, clickHandler }) => {
  React.useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === key) {
        clickHandler(note)
      }
    }
    window.addEventListener('keydown', handleKeyPress)
    return () => {
      window.removeEventListener('keydown', handleKeyPress)
    }
  }, [clickHandler, key, note])

  return color == 'white' ? <White value={note} /> : <Black value={note} />
}

export default Note
