import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

const Black = styled.button<{ active: boolean }>`
  width: 40px;
  height: 130px;
  background: ${(props) => (props.active ? '#333' : 'black')};
  border: solid black 1px;
  position: absolute;
  margin: 1px;
  margin-left: -20px;
`

const White = styled.button<{ active: boolean }>`
  width: 60px;
  height: 200px;
  background: ${(props) => (props.active ? '#eee' : 'white')};
  border: solid black 1px;
  box-shadow: 2px 5px;
  margin: 1px;
  margin-left: '-20px';
  box-sizing: border-box;
`

type Props = {
  color: string
  note: string
  key: string
  clickHandler: () => void
}

const Note: React.FC<Props> = ({ color, note, key, clickHandler }) => {
  const [active, setActive] = useState(false)

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === key) {
        setActive(true)
        clickHandler()
      }
    }
    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.key === key) {
        setActive(false)
      }
    }
    window.addEventListener('keydown', handleKeyDown)
    window.addEventListener('keyup', handleKeyUp)
    return () => {
      window.removeEventListener('keydown', handleKeyDown)
      window.removeEventListener('keyup', handleKeyUp)
    }
  }, [clickHandler, key])

  const handleMouseDown = () => {
    setActive(true)
  }

  const handleMouseUp = () => {
    setActive(false)
  }

  return color == 'white' ? (
    <White
      value={note}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      onKeyDown={() => {}}
      active={active}
    />
  ) : (
    <Black
      value={note}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      onKeyDown={() => {}}
      active={active}
    />
  )
}

export default Note
