import React, { useEffect } from 'react'
import styled, { css } from 'styled-components'

const activeStyle = css`
  background-color: #333;
`

const Black = styled.button`
  width: 40px;
  height: 130px;
  background: black;
  border: solid black 1px;
  position: absolute;
  margin: 1px;
  margin-left: -20px;

  :active,
  ${({ active }) => active && activeStyle} {
    background: #333;
  }
`

const White = styled.button`
  width: 60px;
  height: 200px;
  background: white;
  border: solid black 1px;
  box-shadow: 2px 5px;
  margin: 1px;
  margin-left: '-20px';
  box-sizing: border-box;

  :active,
  ${({ active }) => active && activeStyle} {
    background: #eee;
  }
`

type Props = {
  color: string
  note: string
  key: string
  clickHandler: () => void
  active?: boolean
}

const Note: React.FC<Props> = ({ color, note, key, clickHandler, active }) => {
  return color === 'white' ? (
    <White value={note} onKeyDown={clickHandler} active={active} />
  ) : (
    <Black value={note} onKeyDown={clickHandler} active={active} />
  )
}

export default Note
