import React from 'react'
import styled from 'styled-components'
import { NoteType } from './helpers'
import Note from './Note'

const Wraper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
`

type Props = {
  notes: NoteType[]
  clickHandler: (note: string) => void
}

const Octave: React.FC<Props> = ({ notes, clickHandler }) => {
  const [soundPlayed, setSoundPlayed] = React.useState(false)
  React.useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      notes.forEach((note) => {
        if (e.key === note.key && !soundPlayed) {
          clickHandler(note.note)
        }
        setSoundPlayed(true)
      })
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => {
      window.removeEventListener('keydown', handleKeyPress)
    }
  }, [clickHandler, notes, soundPlayed])

  React.useEffect(() => {
    const timeout = setTimeout(() => {
      setSoundPlayed(false)
    }, 500)

    return () => clearTimeout(timeout)
  }, [soundPlayed])

  return (
    <Wraper>
      <div>
        {notes.map((element: NoteType) => (
          <Note
            color={element.color}
            note={element.note}
            clickHandler={() => clickHandler(element.note)}
            key={element.key}
          />
        ))}
      </div>
    </Wraper>
  )
}

export default Octave
