import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

const Black = styled.button<{ pressed: boolean }>`
  width: 40px;
  height: 130px;
  background: ${(props) => (props.pressed ? '#333' : 'black')};
  border: solid black 1px;
  position: absolute;
  margin: 1px;
  margin-left: -20px;
`

const White = styled.button<{ pressed: boolean }>`
  width: 60px;
  height: 200px;
  background: ${(props) => (props.pressed ? '#eee' : 'white')};
  border: solid black 1px;
  box-shadow: 2px 5px;
  margin: 1px;
  margin-left: '-20px';
  box-sizing: border-box;
`

type Props = {
  color: string
  note: string
  key: string
  clickHandler: () => void
}

const Note: React.FC<Props> = ({ color, note, key, clickHandler }) => {
  const [pressed, setPressed] = useState(false)

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === key) {
        setPressed(true)
        clickHandler()
      }
    }
    const handleKeyUp = (e: KeyboardEvent) => {
      if (e.key === key) {
        setPressed(false)
      }
    }
    window.addEventListener('keydown', handleKeyDown)
    window.addEventListener('keyup', handleKeyUp)
    return () => {
      window.removeEventListener('keydown', handleKeyDown)
      window.removeEventListener('keyup', handleKeyUp)
    }
  }, [clickHandler, key])

  return color == 'white' ? (
    <White value={note} onKeyDown={() => {}} pressed={pressed}>
      {note}
    </White>
  ) : (
    <Black value={note} onKeyDown={() => {}} pressed={pressed}>
      {note}
    </Black>
  )
}

export default Note
